import React, {useState, Fragment} from 'react';
import { Chat } from './components/chat/Chat';
import { ContextProvider, FaqContext } from './context';
import SupportAdmin  from './components/support_admin/SupportAdmin';

import {
  BrowserRouter as Router,
  useLocation,
  Switch,
  withRouter,
  Link,
  Route,
} from "react-router-dom";

function App() {

  const [route, setRoute] = useState('/')
  const [opened, setOpened] = useState(true)


  return (
    <div className="App">
      <ContextProvider>

      <Router>

        <Switch>

          <Route>
            <Fragment>
              <SupportAdmin route={route} handle={setRoute} handleWidget={setOpened}/>
              <Chat route={route} opened={opened}/>
            </Fragment>
          </Route>

        </Switch>

      </Router>

      </ContextProvider>
    </div>
  );
}

export default App;
